var b = require('./')
var repl = require('repl');

b("/dev/ttyACM0",{},function(err,bridge){
  if(err) throw err;

  bridge.bridgeCommand("led.red",function(){

  })


  bridge.on('data',function(data){
    console.log("----->",data);
  })


  repl.start("> ").context.cmd = function(scout,cmd){
    bridge.command(scout,cmd,function(err,res){
      console.log(err,res);
    })
  }

})

